vec3 sdfCircle(vec2 uv, float r){

float x = uv.x;

float y = uv.y;

float d = length(vec2(x,y)) - r;

return d > 0. ? vec3 (0.) : 0.3 + 0.3 * tan(iTime + (uv.y-x) + vec3(1.000,0.682,0.000));

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec2 uv = fragCoord / iResolution.xy;

uv = uv-0.5;

uv = uv*2.0;

uv.x *= iResolution.x / iResolution.y;

float rings = length(uv / 1.0); //How many rings

rings = sin(rings*10. + iTime) / 900.0; //Ring thickness

rings = abs(rings);

rings = smoothstep(0.0,0.13, rings);

rings = 0.00001 / rings;


vec2 offset = vec2(sin(iTime*14.)*1., cos(iTime*14.)*1.);

vec3 col = rings * sdfCircle(uv, 1.0);

fragColor = vec4 (col,1.0);

}
