#define PI 3.14159265358979
#define TAU 6.28318530718


vec2 cx_sin(vec2 a) {
    return vec2(sin(a.x) * cosh(a.y), cos(a.x) * sinh(a.y));
}

vec2 cx_log(vec2 a) {
    float rpart = dot(a, a);
    float ipart = atan(a.y,a.x);
    return vec2(log(rpart)/2.0,ipart);
}

float grid(vec2 uv, vec2 scale) {
    uv = sin(PI * uv * scale); 
    float v = uv.x * uv.y;
    return smoothstep(1.,-1., v/fwidth(v));
}

vec2 transform(vec2 z) {
    z -= 0.5;
    z *= 5.0;

    float t = 0.1 * PI * iTime;
    vec2 z1 = (z + vec2(t, 0.5));
    vec2 z2 = (z - vec2(t, 0.5));
    vec2 z3 = cx_log(cx_sin(z1));
    vec2 z4 = cx_log(cx_sin(z2));
    z = z3 - z4;

    return z;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;
    uv = transform(uv);
    float col = grid(uv, vec2(1.91));
    fragColor = vec4(vec3(col),1.0);
}
