/*
gShaderToy.SetTexture(0, {mSrc:'https://upload.wikimedia.org/wikipedia/commons/7/78/PM5544_%28Testcard_G_variation%29.png', mType:'texture', mID:1, mSampler:{ filter: 'nearest', wrap: 'clamp', vflip:'true', srgb:'false', internal:'byte' }});
*/

#define color(a, b) (b ? 244. - (a) : (a)) / 255.
const vec2 R = vec2(768, 576);

vec3 PM5544(vec2 I)
{
    vec3 col = vec3(122) / 255.;

    I = (I - .5) * R;

    vec2 i = I / 42. + .5,
         p = fract(i) * 42.,
         q = abs(I);

    if(length(I) < 252.)
    {
        int y = int(I.y) / 21,
            i = int(ceil(I.x / 84.));

        vec2 c = vec2(61, 242) / 255.;

        if(y < -8) col = q.x < 19. ? c.yxx : c.yyx;

        else if(y < -6) col = vec3(q.x > 126.);

        else if(y < -4) col = .2 * vec3(floor(I.x / 84.)) + .6;

        else if(y < 0)
        {
            float w, p;

            if(i == -2) w = 1.5,  p = 5.4;
            if(i == -1) w = 2.5,  p = 5.3;
            if(i ==  0) w = 3.5,  p = 3.3;
            if(i ==  1) w = 4.,   p = 0.8;
            if(i ==  2) w = 4.5,  p = 4.1;
            if(i ==  3) w = 5.25, p = 1.1;

            col = .3625 * vec3(sin(36.8 * w * fract(I.x / 84.) + p) + 1.);
        }

        else if(y < 1)
        {
            col = vec3(0);

            if(p.x > 40. || p.x <  2.) col = vec3(2) / 3.;
            if(p.x > 41. || p.x <  1.) col = vec3(1);
            if(q.x < 2.) col = vec3(4 - int(q.x)) / 3.;
            if(abs(p.y - 21.) < 1.) col = vec3(1);
        }

        else if(q.x < 19. && y < 3) col = vec3(q.x < 2. ? 4 - int(q.x) : 0) / 3.;

        else if(y < 5) col = vec3(c[(5 * i + 3) / 7 & 1], c[int(i < 1)], c[i & 1]);

        else if(y < 7) col = vec3(1) - .75 * mod(ceil(I.x / 29.6), 2.);

        else if(y < 9)
        {
            col = vec3(q.x < 126.);
            I.x = abs(I.x + 105.);
            if(I.x < 2.) col = vec3(int(I.x) - 1) / 3.;
        }

        else col = vec3(q.x > 84. || y > 10);
    }
    else
    {
        i = floor(i);

        if(abs(i.x) > 8. || abs(i.y) > 6.) col = vec3(mod(i.x + i.y, 2.) < .5);

        if(p.y > 41. || p.y < 1.) col = vec3(1);

        if(q.x > 273. && q.x < 314. && q.y < 230.)

            col = I.x > 0. ? color(vec3(122, 100, 233), I.y > 0.) 
                           : color(vec3(184,  90, 122), I.y > 0.);


        if(q.x > 232. && q.x < 273. && q.y > 148. && q.y < 230.)

            col = color(vec3(157, 122, 30), I.y > 0.);

        if(q.x < 271. || q.x > 275. || q.y < 148. || q.y > 230.)
        {
            if(p.x > 40. || p.x < 2.) col = (col + 2.) / 3.;
            if(p.x > 41. || p.x < 1.) col = vec3(1);
        }
    }

    return col;
}


void mainImage(out vec4 O, vec2 I)
{
    O = vec4(0);
    if(1.5 * abs(I.x - .5 * iResolution.x) > iResolution.y) return;

    I.x -= .5 * iResolution.x;
    I /= R * iResolution.y / R.y;
    I.x += .5;

    O.xyz = PM5544(I);

    //O = abs(O - texture(iChannel0, I));
    //O = texture(iChannel0, I);
}

vec2 mainSound(int s, float t)
{
    return vec2(sin(6283. * t));
}
