void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // 正規化されたピクセル座標
    vec2 uv = fragCoord / iResolution.x;
    float step = 10.0;
    int currentstep = int(uv.y * step);
    vec3 col = vec3(0.0);

    // `sin`波で横方向のオフセットを生成
    float wave = 0.05 * sin(uv.y * 20.0 * 3.1415); // 波の振幅と周期を設定

    // 偶数・奇数の段で色を交互に変える
    if (currentstep % 2 == 0) {
        col = mix(vec3(1.0, 1.0, 1.0), vec3(0.0, 0.0, 1.0), uv.x + wave);
    } else {
        col = mix(vec3(0.0, 0.0, 1.0), vec3(1.0, 1.0, 1.0), uv.x + wave);
    }

    fragColor = vec4(col, 1.0);
}
