vec3 triangle(vec2 uv, vec2 pos, float s, float rotation) {
    #define pi 3.14159265358979323846
    #define pii pi * 2.0

    uv *= vec2(iResolution.x, iResolution.y);
    uv /= max(iResolution.x, iResolution.y);

    uv -= pos;
    uv *= 2.0 - 1.0;

    float a = atan(uv.x, uv.y) + rotation;
  	float r = pii / float(3);
    float d = cos(floor(0.5 + a / r) * r - a) * length(uv);
    return vec3(step(s, d));
}
vec3 rect(vec2 uv, vec2 pos, vec2 size) {
    float f = step(pos.x - size.x, uv.x) - step(pos.x + size.x, uv.x);
    return vec3(f * step(pos.y - size.y, uv.y) - step(pos.y + size.y, uv.y));
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    float ratio = iResolution.x / iResolution.y;
    uv.x *= ratio;

    // setup
    float barOff = 0.35, starOff = 0.06, radius = 0.14;
    vec2 pos = vec2(0.5 * ratio, 0.5);
    vec2 size = vec2(0.5 * ratio, 0.08); // used for the bars
    vec3 blue = vec3(0.8, vec2(0) );

    // bars
    vec3 bars = rect(uv, vec2(pos.x, pos.y + barOff), size);
    bars += rect (uv, vec2(pos.x, pos.y - barOff), size);

    // star of David
    vec3 star = triangle(vec2(uv.x, uv.y * 2.0), vec2(pos.x, pos.y + starOff), radius, pi);
    star += 1.0 - triangle(vec2(uv.x, uv.y * 2.0), vec2(pos.x, pos.y + starOff), radius - 0.035, pi);
    star += triangle(vec2(uv.x, uv.y * 2.0), vec2(pos.x, pos.y + starOff), radius, pii);
    star -= triangle(vec2(uv.x, uv.y * 2.0), vec2(pos.x, pos.y + starOff), radius - 0.035, pii);
    star += blue * 2.0;

    // output 
    vec3 col = star - bars;
    fragColor = vec4(blue + col, 1.0);
}
