int xorshift32(int x) {
    x ^= x << 13;
    x ^= x >> 17;
    x ^= x << 5;
    return x;
}

float packToFloat(int x) {
    return intBitsToFloat(
        0x3F800000 | (
            0x007FFFFF & x
        )
    ) - 1.0;
}

float prng(int x) {
    return packToFloat(xorshift32(x) + (x * 283));
}

const float TURN = acos(-1.) * 2.;
float zag(float x, float border) {
    float w = fwidth(x) * 0.75;
    return step(
        border,
        smoothstep(
            0.5 - w, 0.5 + w,
            1. - abs(1. - 2. * fract(x))
        )
    );
}

#define HEX(x) (vec3(uvec3(255) & (uvec3(x) >> uvec3(16, 8, 0))) / 255.)
vec3 colormap(float hue) {
    const vec3[] cols = vec3[](
    HEX(0xffffff),
    HEX(0x00cbf8),
    HEX(0xf96b07),
    HEX(0xffef00),
    HEX(0x07e92d),
    HEX(0xfb20d2),
    HEX(0x010a31),
    HEX(0x540abd)
    );

    return mix(
        cols[int(hue) & 7],
        cols[int(hue + 1.) & 7],
        fract(hue)
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = fract(iTime / 16.);
    vec2 uv = (2. * fragCoord - iResolution.xy) / length(iResolution);

    float r = length(uv);
    float rWiggle = log(r);
    rWiggle = sin(
        3.3 * rWiggle +
        12. * t * TURN
    ) * 0.6;

    const float HSPEED = 8.;
    float h = dot(
        uv,
        vec2(sin(t * TURN + rWiggle), cos(t * TURN + rWiggle))
    );
    float fh = round(
        2.5 * (
            pow(abs(h), 0.5) * (1. - 2. * step(0., h))
        )
    + HSPEED * t);

    float stripes = zag(
        6.0 * r / (
            0.005 + // avoid div0
            fh - HSPEED * t
        ) + 12. * t,
        prng(
            floatBitsToInt(
                uv.x * 1.2
            ) + 
            floatBitsToInt(
                uv.y * 0.9
            ) + iFrame
        )
    );

    vec3 col = colormap(
        //round(prng(iFrame >> 3) * 97.)
        + 3. * fh
        + 1. * stripes
    );

    // Output to screen
    fragColor = vec4(col,1.0);
}
