vec4 layerOnTop(vec4 bottom, vec4 top)
{
    float alpha = min(1.0, top.a);
    return top * alpha + bottom*(1. - alpha);
}

//Screen
vec4 screen (vec4 target, vec4 blend){
    return 1.0 - (1.0 - target) * (1.0 - blend);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float fadeEllipse = length((uv.xy  - vec2(0.5, 1.0)) / vec2(0.2, 0.3)) / ((0.5 * sin(iTime) + 0.5) * 1.5 + 4.5);
    vec4 fireColor = vec4(0.0, 0.0, 0.0, 0.0);

    float f = fadeEllipse + (fadeEllipse * fadeEllipse);
    float ff = f * f;
    float fff =  f * f * f;

    //float sideways = sin(iTime * 6.)* 0.01;
    float sideways = 0.0;

    fireColor = layerOnTop(fireColor, vec4(texture(iChannel1, uv - vec2(-sideways, iTime/1.5 + 1.2345)).r * fff*fff*fff) * vec4(1.0, 0.0, 0.0, 1.0));    
    fireColor = layerOnTop(fireColor, vec4(texture(iChannel1, uv - vec2(sideways, iTime/1.5 + 2.2345)).r * fff*fff*ff) * vec4(1.0, 0.1, 0.0, 1.0));
    fireColor = layerOnTop(fireColor, vec4(texture(iChannel1, uv - vec2(-sideways, iTime/1.5 + 3.2345)).r * fff*fff) * vec4(1.0, 0.2, 0.0, 1.0));

    //fireColor = layerOnTop(fireColor, vec4(texture(iChannel1, uv - vec2(sideways, iTime/1.5 + 1.2345)).r) * vec4(1.0, 0.0, 0.0, ff));    
    //fireColor = layerOnTop(fireColor, vec4(texture(iChannel1, uv - vec2(-sideways, iTime/1.5 + 2.2345)).r) * vec4(1.0, 0.1, 0.0, fff));
    //fireColor = screen(fireColor, vec4(texture(iChannel1, uv - vec2(sideways, iTime/1.5 + 3.2345)).r) * vec4(1.0, 0.2, 0.0, fff*ff));
    vec4 sceneColor = texture(iChannel0, uv);

    fragColor = layerOnTop(sceneColor, fireColor);
    //fragColor = vec4(f);
}
