#define pi 3.14159265358979323846

float hDist(in vec2 p, in float r )
{
    const vec3 k = vec3(-0.866025404,0.5,0.577350269);
    p = abs(p);
    p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;
    p -= vec2(clamp(p.x, -k.z*r, k.z*r), r);
    return length(p)*sign(p.y);
}

float cDist(in vec2 p, in float r)
{
    return(length(p) - r);
}

vec2 p2c(vec2 p)
{
    return(vec2(cos(p.x), sin(p.x)) * p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    uv *= 5.0;

    vec3 col1 = vec3(1.0, 0.0, 1.0) * 1.0;
    vec3 col2 = vec3(0.0, 1.0, 1.0) * 1.0;
    vec3 col3 = vec3(0.0, 0.0, 1.0) * 1.0;
    vec3 col4 = vec3(1.0, 0.0, 0.0) * 1.0;

    float d1 = hDist(uv, 1.0);
    d1 = smoothstep(0.0, 2.0, abs(d1) + 0.4);
    d1 = 0.1 / d1;
    col1 *= d1;

    float d2 = cDist(uv, 5.0);
    d2 = smoothstep(0.0, 2.0, abs(d2) + 0.4);
    d2 = 0.1 / d2;
    col2 *= d2;

    float d3 = cDist(uv - p2c(vec2(pi * iTime / 1.5 + pi, 4.0 - 1.3 * abs(sin((pi * iTime / 3.0) * 8.0+ pi / 2.0)))), 1.0);
    d3 = smoothstep(0.0, 2.0, abs(d3) + 0.4);
    d3 = 0.1 / d3;
    col3 *= d3;

    float d4 = hDist(uv - p2c(vec2(pi * iTime / 1.5, 2.0 + 1.5 * abs(sin(2.0 * pi * iTime + pi / 2.0)))), 1.0);
    d4 = smoothstep(0.0, 2.0, abs(d4) + 0.4);
    d4 = 0.1 / d4;
    col4 *= d4;

    vec3 col = col1 + col2 + col3 + col4;
    fragColor = vec4(col, 1.0);
}

vec2 mainSound( int samp, float time )
{
    float time1 = mod(time + 0.25, 0.5);
    float time2 = mod(time + 0.25, 0.375);
    return 0.3 * (vec2(sin(6.2831 * 440.0 * time1) * exp(-10.0 * time1)) + vec2(sin(6.2831 * 220.0 * time2) * exp(-10.0 * time2)));
}
