float smin( float a, float b, float k )
{
	float h = clamp( 0.5 + 0.5*(b-a)/k, 0.0, 1.0 );
	return mix( b, a, h ) - k*h*(1.0-h);
}
float smax(float a, float b, float k)
{
    return smin(a, b, -k);
}
vec4 hash(vec4 p4)
{
    p4 = fract(p4  * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}
float ln (vec3 u, vec3 a, vec3 b) {

    return length(u-a-(b-a)*clamp(dot(u-a,b-a)/dot(b-a,b-a),0.,1.));

}

float map (vec3 p) {
    float d = abs(p.z+.1+.1*sin(10.*dot(p.xy,p.xy))*exp(-1.*dot(p.xy,p.xy)))-.1;
    d = smin(d,length(p)-.3,.01);
    d = smin(d,length(p-vec3(0,0,-.2))-.15,.05);
    d = smin(d,length(p-vec3(0,0,-.3))-.1,.05);
    d = smin(d,ln(p,vec3(0,0,-.3),vec3(0,0,-.7))-.03,.1);
    d = smin(d,length(p-vec3(0,0,-.7))-.05,.1);
    float a = atan(p.y,p.x)*6.;
    float wv = sin(a-1.);
    d = smin(d,smax(abs(length(p+vec3(0,0,.6))-.5)-.01,-p.z-.4+.05*wv,.07),.1);
    a = round(a/pi/2.)*2.*pi;
    p.xy = p.xy*ei(a/6.);
    d = smin(d,length(p-vec3(.5,0,-.55))-.03,.2);
    return min(d,-p.z);

}
vec3 normal (vec3 p) {
    vec2 e = vec2(1e-3,0);
    float m = map(p);
    return normalize(vec3(
        map(p+e.xyy)-m,
        map(p+e.yxy)-m,
        map(p+e.yyx)-m
    ));

}
Main {
    Q = A(U);
    vec4 h = hash(vec4(iFrame,1,1,1));
    float w = h.w;
    U = 2.*(U+h.xy*2.-1.-.5*R)/R.y;
    vec3 p = vec3(0,0,-2.3);
    vec3 d = normalize(vec3(U,3.));
    p.y -= .2;
    d.y = -d.y;
    p.yz *= ei(-1.);
    d.yz *= ei(-1.);
    p += h.z*d*map(p);
    for (float i = 0.; i < 200.; i++) {
        float m = map(p);
        p += d*max(abs(m),1e-5);
        if (p.z >-1e-4) {
            d = normalize(hash(vec4(R*U,iFrame,i)).xyz*2.-1.);
            d.z = -abs(d.z);
            p += d*.001;
            if (sin(20.*p.x)*sin(20.*p.y)<0.) d = normalize(d*vec3(3,3,1));
        } 
        else 
        if (abs(m) < 4e-5) {
            float z = (hash(vec4(R*U,iFrame,i)).z*2.-1.);
            vec3 n = normal(p);
            d = z>0.?reflect(d,n):normalize(d-(.5+.1*w)*n);//refract(d,n,1.);
            p += .001*d;
        }

        if (p.z < -3.) {
            p += d*(p.z+5.);
            p += d*(p.z+5.);
            p += d*(p.z+5.);
            p += d*(p.z+5.);
            p += d*(p.z+5.);
            p += d*(p.z+5.);
            p += d*(p.z+5.);
            p += d*(p.z+5.);
            p += d*(p.z+5.);
            p += d*(p.z+5.);
            Q += max(sin(-2.+6.1*w+vec4(1,2,3,4)),0.)*50.*
                float(length(p.xy-vec2(2,1))<2.);
            break;
        }
    }

}
