vec3 GreyScale(vec3 InCol);
vec3 Threshold(vec3 InCol);

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 TopLeft      = vec2( 1., 1.)/iResolution.xy;
    vec2 TopMiddle    = vec2( 0., 1.)/iResolution.xy;
    vec2 TopRight     = vec2(-1., 1.)/iResolution.xy;
    vec2 MiddleLeft   = vec2( 1., 0.)/iResolution.xy;
    vec2 MiddleMiddle = vec2( 0., 0.)/iResolution.xy;
    vec2 MiddleRight  = vec2(-1., 0.)/iResolution.xy;
    vec2 BottomLeft   = vec2( 1.,-1.)/iResolution.xy;
    vec2 BottomMiddle = vec2( 0.,-1.)/iResolution.xy;
    vec2 BottomRight  = vec2(-1.,-1.)/iResolution.xy;

    // Time varying pixel color
    vec3 col = texture(iChannel0,uv).rgb-Threshold(vec3(sqrt(pow((
               GreyScale(texture(iChannel0,uv+TopLeft     ).rgb)* 1.+
               GreyScale(texture(iChannel0,uv+TopMiddle   ).rgb)* 2.+
               GreyScale(texture(iChannel0,uv+TopRight    ).rgb)* 1.+
               GreyScale(texture(iChannel0,uv+MiddleLeft  ).rgb)* 0.+
               GreyScale(texture(iChannel0,uv+MiddleMiddle).rgb)* 0.+
               GreyScale(texture(iChannel0,uv+MiddleRight ).rgb)* 0.+
               GreyScale(texture(iChannel0,uv+BottomLeft  ).rgb)*-1.+
               GreyScale(texture(iChannel0,uv+BottomMiddle).rgb)*-2.+
               GreyScale(texture(iChannel0,uv+BottomRight ).rgb)*-1.
               ).r,2.)+
               pow((
               GreyScale(texture(iChannel0,uv+TopLeft     ).rgb)* 1.+
               GreyScale(texture(iChannel0,uv+TopMiddle   ).rgb)* 0.+
               GreyScale(texture(iChannel0,uv+TopRight    ).rgb)*-1.+
               GreyScale(texture(iChannel0,uv+MiddleLeft  ).rgb)* 2.+
               GreyScale(texture(iChannel0,uv+MiddleMiddle).rgb)* 0.+
               GreyScale(texture(iChannel0,uv+MiddleRight ).rgb)*-2.+
               GreyScale(texture(iChannel0,uv+BottomLeft  ).rgb)* 1.+
               GreyScale(texture(iChannel0,uv+BottomMiddle).rgb)* 0.+
               GreyScale(texture(iChannel0,uv+BottomRight ).rgb)*-1.
               ).r,2.))));

    // Output to screen
    fragColor = vec4(col,1.);
}

vec3 GreyScale(vec3 InCol){
    return vec3((InCol.r+InCol.g+InCol.b)/3.);
}

vec3 Threshold(vec3 InCol){
    return (1.-vec3(0.,0.,0.))*float(InCol.r>0.3);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0,0.0,1.0,1.0);
}
