/* originals https://www.shadertoy.com/view/ctXBRH https://www.shadertoy.com/view/mdcfDl https://www.shadertoy.com/view/ms3cz8*/
vec3 palette( in float t)
{
    vec3 a = vec3(0., 0., 0.500);
    vec3 b = vec3(0.188, .23, 1.);
    vec3 c = vec3(1.000, .000, 0.508);
    vec3 d = vec3(.100, .2, 0.);
    return a + b*cos( 6.28318*(c*t+d) );
}

float sdCircle( vec2 p, float r )
{
    return length(p) - r;
}
#define C(t) ( vec3(1.5,.6,.6) + vec3(1.25,.38,-2.5) * cos(6.28318*(vec3(-3.142,-3.223,2)*(t)+vec3(.31,-1.42,1.75)))) //
#define CS(a) vec2(cos(a),sin(a))

vec2 rotateUv(vec2 uv, vec2 pivot, float rotation) {
    float cosa = cos(rotation);
    float sina = sin(rotation);
    uv -= pivot;
    return vec2(
        cosa * uv.x - sina * uv.y,
        cosa * uv.y + sina * uv.x 
    ) + pivot;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finaleColor = vec3(0.0);
 uv*=tan(iTime*0.11);
    uv = rotateUv(uv, vec2(0.), iTime*0.2);
    uv0 = rotateUv(uv0, vec2(0.), iTime*0.2);
    vec4 O=fragColor;

    vec2 F =fragCoord;
       vec2 u2 =fragCoord;
   vec2 W = iResolution.xy, 
         U = (u2 - .5 * W) / W.y;

    vec3 col2 = vec3(0.0);

    for(float r = .05,m = 2.,R = .1,i,f,a ; i < 1e2; i++ )  {

        i+= f = sin(iTime/.5)*.5 + .1;
        a = i/5.;

        col2 += pow(.002/ length( U - f*(R + m*r) * CS(a) + m*R * CS(f*a + m*a) ) , 1.5) 
               * C( length(U) + i + iTime*.6 ); //циклоидальная кривая

        U*=mat2(cos(iTime*.1+vec4(0,11,33,0)));
    }


 vec2 r = iResolution.xy, u = (F+F-r)/r.y;    
    O.rgb*=0.;
    for (float i; i<15.; O.rgb +=
    .0035/(abs(length(u*u)-i*.0275)+.003)                   // shape distance
    * (sin(i*5.+vec3(0,1,3))+1.)                            // color
    * smoothstep(.35,.4,abs(abs(mod(iTime,2.)-i*.1)-1.)) // animation
    ) u*=mat2(cos((iTime+i++)*(i< 2.0 ? .0 :.03)+ vec4(0,33,11,0)));   // rotation
     for(float i = 0.0; i < 6.0; i++){
        float x = sin(i)*5.;
        uv = fract(uv*1.3) - 0.5;

        float d = sdCircle(uv, 3.0) * exp(-length(uv0))+O.x;

        vec3 col = palette(length(uv0) + i * .5 + iTime * .5);

        d = sin(d * 1. - iTime) / 3.;
        d = abs(d);
        d = pow(0.02 / d, 1.5);

        finaleColor += col *col2+d; 
     }

    fragColor = vec4(finaleColor, 1.0);

}
