/*originals https://www.shadertoy.com/view/MdXSzS https://www.shadertoy.com/view/lslyRn*/
#define iterations 18 
#define formuparam 0.53

#define volsteps 20
#define stepsize 0.1

#define zoom   0.800
#define tile   0.850
#define speed  0.000

#define brightness 0.0015
#define darkmatter 0.300
#define distfading 0.730
#define saturation 0.850


void mainVR( out vec4 fragColor, in vec2 fragCoord, in vec3 ro, in vec3 rd )
{
//get coords and direction
vec3 dir=rd;
vec3 from=ro;

//volumetric rendering
float s=.1,fade=1.;
vec3 v=vec3(0.);
for (int r=0; r<volsteps; r++) {
vec3 p=from+s*dir*1.5;
p = abs(vec3(tile)-mod(p,vec3(tile*2.))); // tiling fold
float pa,a=pa=0.;
for (int i=0; i<iterations; i++) {
p=abs(p)/dot(p,p)-formuparam;
            p.xy*=mat2(cos(iTime*0.02),sin(iTime*0.02),-sin(iTime*0.02),cos(iTime*0.02));// the magic formula
a+=abs(length(p)-pa); // absolute sum of average change
pa=length(p);
}
float dm=max(0.,darkmatter-a*a*.001); //dark matter
a*=a*a; // add contrast
if (r>6) fade*=1.1-dm; // dark matter, don't render near
//v+=vec3(dm,dm*.5,0.);
v+=fade;
v+=vec3(s,s*s,s*s*s*s)*a*brightness*fade; // coloring based on distance
fade*=distfading; // distance fading
s+=stepsize;
}
v=mix(vec3(length(v)),v,saturation); //color adjust
fragColor = vec4(v*.04,1.);
}
float  glory_lovely_beautiful_star(vec2 uv, float anim)
{
    uv = abs(uv);
    vec2 pos = min(uv.xy/uv.yx, anim);
    float p = (2.0 - pos.x - pos.y);
    return (2.0+p*(p*p-1.5)) / (uv.x+uv.y);      
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
//get coords and direction
vec2 uv=fragCoord.xy/iResolution.xy-.5;
vec2 uv2=fragCoord.xy/iResolution.xy-.5;
vec2 uv3=fragCoord.xy/iResolution.xy-.5;
vec2 uv4=fragCoord.xy/iResolution.xy-.5;
uv2.x+=0.12*sin(iTime*0.1);
uv2.y+=0.12*cos(iTime*0.1);
uv3.x+=0.12;
uv3.y+=0.32;
uv4.x+=-0.32;
uv4.y+=-0.42;

vec3 dir=vec3(uv*zoom,1.);
float time=iTime*speed+.25;

float t = iTime * .1 + ((11.25 + .05 * sin(iTime * .1))/(length(uv.xy) + 1.07)) * 2.2;
float si = tan(t);
float co = sin(t);
mat2 ma = mat2(co, si, -si, co);

float v1, v2, v3;
v1 = v2 = v3 = 0.0;

float s = 0.0;
for (int i = 0; i < 90; i++)
{
vec3 p = s * vec3(uv, 0.0);
p.xy *= ma;
p += vec3(.22, .3, s - 1.5 - sin(iTime * .13) * .1);
for (int i = 0; i < 8; i++) p = abs(p) / dot(p,p) - 0.659;
v1 += dot(p,p) * .0015 * (1.8 + sin(length(uv.xy * 13.0) + .5  - iTime * .2));
v2 += dot(p,p) * .0013 * (1.5 + sin(length(uv.xy * 14.5) + 1.2 - iTime * .3));
v3 += length(p.xy*10.) * .0005;
s  += .035;
}

float len = length(uv);
v1 *= smoothstep(.0, .2, len);
v2 *= smoothstep(.05, .20, len);
v3 *= smoothstep(.02, .3, len);

vec3 col = vec3( v3 * (1.5 + sin(iTime * .2) * .4),
(v1 + v3) * .3,
v2) + smoothstep(0.2, .0, len) * .85 + smoothstep(.0, .6, v3) * .3;


 uv *= 2.0 * ( cos(iTime * 22.0) -2.5);

    // anim between 0.9 - 1.1
    float anim = sin(iTime * 12.0) * 0.1 + 1.0;    



vec3 from=vec3(22.,.5,0.5);
from+=vec3(time*2.,time,-2.);


mainVR(fragColor, fragCoord, from, dir);
    fragColor*=vec4(col,1.);
        fragColor+= vec4( glory_lovely_beautiful_star(uv2,anim) * vec3(2.75,0.3,0.55)*.01, 1.0);
         fragColor+= vec4( glory_lovely_beautiful_star(uv,anim) * vec3(3.55,0.5,0.55)*.05, 1.0);
          fragColor+= vec4( glory_lovely_beautiful_star(uv3,anim) * vec3(21.05,0.13,0.15)*.105, 1.0);
          fragColor+= vec4( glory_lovely_beautiful_star(uv4,anim) * vec3(1.05,0.23,0.15)*.105, 1.0);

}
