//philip.bertani@gmail.com
//logic taken from gaz: https://www.shadertoy.com/view/ftKBzt
//and made more verbose and tweaked

#define rot(x) mat2(cos(x+vec4(0,11,33,0)))

//Rodrigues-Euler axis angle rotation
#define ROT(p,axis,t) mix(axis*dot(p,axis),p,cos(t))+sin(t)*cross(p,axis)

//formula for creating colors;
#define H(h,id)  (  cos( h*2.*(1.+2.*sin(iTime+sin(iTime))) + 30.*vec3(.1,3,2)  )*.7 + .2  )

//formula for mapping scale factor 
#define M(c)  log(c)

#define R iResolution


void mainImage( out vec4 O, vec2 U) {

    O = vec4(0);

    vec3 c=vec3(0), rd = normalize( vec3(U-.5*R.xy, R.y))*50.;

    float sc,dotp,totdist=0., t=iTime/2.;


    for (float i=0.; i<100.; i++) {

        vec4 p = vec4( rd*totdist, 0.);

        p.xyz += vec3(0,0,-5.-10.*abs(sin(t/2.)));
        sc = 1.;  //scale factor         

        p.xyz = ROT( p.xyz, normalize( vec3(sin(t/5.),cos(t/3.),0)  ),t*2.);

        vec4 id = round(p/7.);

        for (float j=0.; j<3.; j++) {

            p =  log(.95+abs(p)) ;

            dotp = max(1./dot(p,p),.3);
            sc *= dotp;// * (.7 + .15*abs(sin(t*1.2+3.14/2.)));  

            p *= dotp - .7;

        }

        float dist =  abs( length(p)-.6)/sc ;  //funky distance estimate
        float stepsize = dist/40. + 8e-4;      //distance plus a little extra
        totdist += stepsize;                   //move the distance along rd


        //accumulate color, fading with distance and iteration count
        c += .02 * mix( vec3(1), H(M(sc),id),.7) * exp(-i*i*stepsize*stepsize*1e1);
    }

    c = clamp(c,0.,1.);
    c *= c;

    O = ( vec4(c,0) );
    //this one needs some gaussian blur 

}
