#define amp 5e1f
#define dist_amp 1.618034e1f

float noise(vec2 coord)
{
    return floor(amp * 1.0f / (1.0f + pow(dist_amp * abs(dot(vec2(cos(coord.x+iTime), sin(coord.y-iTime*1.618034f)), vec2(0.06711056, 0.00583715)) * 52.9829189), 2.0f)));
}

float smoothNoise(vec2 coord)
{
    vec4 vala = vec4(
        noise(coord - vec2(0.0, 0.0)),
        noise(coord - vec2(1.0, 0.0)),
        noise(coord - vec2(0.0, 1.0)),
        noise(coord - vec2(1.0, 1.0))
    );
    vec4 valb = vec4(
        noise(coord + vec2(0.0, 0.0)),
        noise(coord + vec2(1.0, 0.0)),
        noise(coord + vec2(0.0, 1.0)),
        noise(coord + vec2(1.0, 1.0))
    );
    vec2 fac = fract((coord) );

    vec4 val = abs(valb - vala);

    val = mix( mix(mix(vala.xxxx, vala.yyyy, fac.xxxx), mix(vala.wwww, vala.zzzz, fac.xxxx), fac.yyyy), 
               mix(mix(valb.xxxx, valb.yyyy, fac.xxxx), mix(valb.zzzz, valb.wwww, fac.xxxx), fac.yyyy),
               val );


    return (mix(mix(val.x, val.y, fac.x), mix(val.z, val.w, fac.x), fac.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord.xy)/iResolution.xy;
    uv = uv * 2.0f - 1.0f;

    // Output to screen
    fragColor = vec4(pow(smoothNoise(1.618034f * (vec2(0.0f, 0.5f) + uv)), 1.0f/2.2f));
}
