float itr = 200.0;

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 z = ((2.0 * fragCoord - iResolution.xy) / iResolution.y) * 1.5;
    vec2 zm, zp, zpt = vec2(0.0);
    vec2 k = vec2(sin(iTime / 2.0 - 10.0) / 2.0, cos(iTime / 3.0) / 2.0);
    vec2 c = vec2(sin(iTime / 2.5 + 5.0) / 2.0, cos(iTime / 1.5 + 20.0) / 2.0);
    float l = 0.0;
    for (l = 0.0; l < itr; l += 1.0) {
        zp = mat2(k, -k.y, k.x) * zm;
        zpt = mat2(z, -z.y, z.x) * z + c + zp;
        if(dot(z, z) > 65536.0) break;        
        zm = z;
        z = zpt;
    }
    l = l - log2(log2(dot(z,z))) + 4.0;
    if(dot(z, z) < 65536.0) l = itr;
    fragColor = vec4(vec3(sqrt(l / 100.0)) * vec3(1.4, 0.4, 0.2), 1.0);
}
