void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
    vec2 col_row = vec2(14.,9.);
    vec2 grid = vec2(uv)*col_row;
    vec2 uv_index = floor(grid);
    uv = fract(grid);
    uv -= 0.5;

    vec3 color = vec3(0.791,0.942,1.000);
    vec2 ms = (iMouse.xy/iResolution.xy) * col_row;
    if(uv_index != floor(ms))
        color = color;
    else
    	color = length(uv) > 0.2 ? color : vec3(1.000,0.296,0.248);


    fragColor = vec4(color,1.0);
}
