void mainImage( out vec4 col, in vec2 uv )
{
    float skip=3.;
    if(mod(floor(float(iFrame)),skip)<=0.){
    col=vec4(0.);
    uv.x = 1.*(uv.x-0.5*iResolution.x)/iResolution.y;
    uv.y = 1.*(uv.y-0.5*iResolution.y)/iResolution.y;
    float a = floor(float(iFrame))/(480.);

    float ang = atan(uv.y/uv.x);
    float rate=1.;

    float r1=0.;
    float r2=0.;
    float r3=0.;
    float x1=0.;
    float y1=0.;
    vec2 holder=vec2(0.);
    float dist1=0.;
    float dist2=0.;
    float dist3=0.;

    for(float theta=rate*a; theta<=rate*a+24.*3.1415; theta+=3.141592/50.){
    float g=-20.;
    float h=0.05;
    float b=0.25;
    r1=sin(pow(theta,0.5)+sin(2.*theta+sin(theta+a)));
    x1 = r1*cos(theta);
    y1 = r1*sin(theta);
    holder=vec2(x1,y1);
    dist1=length(holder-uv);
    if(abs(uv.x+uv.y-x1-y1)+abs(uv.x-uv.y-x1+y1)<h){
    col+=b*vec4(pow(2.,g*dist1),0.,0.,0.);
    }



    r2=cos(pow(theta,0.5)+cos(2.*theta+sin(theta-a)));
    x1 = r2*cos(theta);
    y1 = r2*sin(theta);
    holder=vec2(x1,y1);
    dist1=length(holder-uv);
    if(abs(uv.x+uv.y-x1-y1)+abs(uv.x-uv.y-x1+y1)<h){
    col+=b*vec4(0.,pow(2.,g*dist1),0.,0.);
    }


    r3=-cos(pow(theta,0.5)+cos(2.*theta+sin(theta-a)));
    x1 = r3*cos(theta);
    y1 = r3*sin(theta);
    holder=vec2(x1,y1);
    dist1=length(holder-uv);
    if(abs(uv.x+uv.y-x1-y1)+abs(uv.x-uv.y-x1+y1)<h){
    col+=b*vec4(0.,0.,pow(2.,g*dist1),0.);
    }



    }
    }else{
    uv = uv / iResolution.xy;
    col = texture(iChannel0, uv); 
    }
}
