void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    int xx = 1, yy = int(fragCoord.y / 8.0) % 2;

    if (yy == 0 && (xx = 0) == 0) {
        fragColor = vec4(float(xx), 0.0, 0.0, 1.0);
    } else {
        fragColor = vec4(float(xx), 0.0, 0.0, 1.0);
    }
}

/*
    RESULT:
        If black-screen, NO short-circuiting.
        If red/black screen, YES short-circuiting.
*/
