void mainImage(out vec4 o, vec2 u) {
    vec2 R = iResolution.xy,
         p = (u + u - R) / R.y;

    //p *= 6.;
    float blink = cos(iTime * 5.) > .5 ? 0. : 1.;
    vec2 k = mod(p + vec2(2, 1) * blink, .1) - .05;

    o.rgb = fract(2323.1 * sin(vec3(1, 3, 5) + 112. * dot(p-k, (p-k).yx+1.))) * .5 + .5;
}
