void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 center = vec2(0.5, 0.5);
    float startScale =1.0;
    float endScale = 2.0;
    vec2 startPos = vec2(0.0, 0.0);
    vec2 endPos = vec2(1, 1);
    float t = mod(iTime, 5.0) / 5.0; 

    float scale = mix(startScale, endScale, t);
    vec2 pos = mix(startPos, endPos, t);

    vec2 scaledUv = (uv - center) / scale + center;

    vec2 pannedUv = scaledUv + (pos - center);

    vec4 color = texture(iChannel0, pannedUv);
    fragColor = color;
}
