vec4 AccentColor = vec4(1,0,0,1);
float Threshold = 0.3;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
    vec4 mainTex = texture(iChannel0,uv);

    vec4 a = abs(mainTex - AccentColor);
    vec3 b = vec3(dot(mainTex.xyz, vec3(0.299, 0.587, 0.114)));
    float c = dot(a.xyz,b.xyz);

    if (c < Threshold)
    fragColor = mainTex;
    else
    fragColor = vec4(c, c, c, mainTex.a);

}
