void mainImage( out vec4 o, in vec2 FC )
{
    vec2 uv=(FC.xy*2.-iResolution.xy)/min(iResolution.x,iResolution.y)*8.;

    mat2 z = mat2(uv, -uv.y, uv.x);
    z*=fract((log(length(z[0]))+atan(z[0][0],z[0][1])/2./3.1415)-iTime*.618);
    uv=z[0];

    o=texture(iChannel0,uv*.1+.5); return;

    float i,d,e=1.;
    vec3 p;
    for(;i++<99.&&e>1e-3;){
      p=normalize(vec3(uv,1))*d;
      // p.z+=t*1.618;
      p=fract(p)-.5;
      d+=e=length(p)-.5;
    }
    o+=3./i;
}
