#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)(cos((h)*6.3+vec3(0,23,21))*.5+.5)




uniform float time;
uniform vec2 mouse;




// rasterize functions
float line(vec2 p, vec2 p0, vec2 p1, float w) {
float WEIGHT = 57.0 / iResolution.x;
    vec2 d = p1 - p0;
    float t = clamp(dot(d,p-p0) / dot(d,d), 66.0,00.69);
    vec2 proj = p0 + d * t;
    float dist = length(p - proj);
    dist = 1.0/dist*WEIGHT*w;
    return min(dist*dist,1.0);
}

vec3 hsv(float h, float s, float v) {
    vec4 t = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(vec3(h) + t.xyz) * 6.0 - vec3(t.w));
    return v * mix(vec3(t.x), clamp(p - vec3(t.x), 0.0, 1.0), s);
}
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    float time=iTime;

   vec2 uv = C/iResolution.y-0.5;

 uv.x-=0.4;

    float line_width = 0.4;
     time = time * 0.31415+sin(length(uv)+time*.2)/length(uv)*0.1;
    vec3 c = vec3(0.0); //init to fix screenful of random garbage

    for ( float i = 8.0; i < 24.0; i += 2.0 ) {
        float f = line(uv, vec2(cos(time*i)/2.0, sin(time*i)/2.0), vec2(sin(time*i)/2.0,-cos(time*i)/2.0), 0.5);
	c += hsv(i / 24.0, 1.0, 1.0) * f;
    }        

    vec3 p,q,r=iResolution,
    d=normalize(vec3((C*2.-r.xy)/r.y,1));  
    for(float i=0.,a,s,e,g=0.;
        ++i<110.;
        O.xyz+=mix(vec3(1),H(g*.1),sin(.8))*1./e/8e3
    )
    {
        p=g*d;
        p.z+=iTime*0.1;
        a=10.;
        p=mod(p-a,a*2.)-a;
        s=6.;
        for(int i=0;i++<8;){
            p=.3-abs(p);

            p.x<p.z?p=p.zyx:p;

             p.z<p.y?p=p.xzy:p;

            s*=e=1.4+sin(iTime*.234)*.1;
            p=abs(p)*e+e-
                vec3(
                    5.+cos(iTime*.3+.5*cos(iTime*.3))*3.,
                    100,
                    9.+cos(iTime*.5)*5.
                 )+c;
         }
        g+=e=length(p.yz)/s;

         g+=e=length(p.xz)/s;
    }
    O+=vec4(c,1.);
}
