float LogisticIterations(vec2 p, int n) {
    float q = p.x;
    for(int c = 0; c < n; c += 1){
        q = (0.5 * p.y + 3.5) * q * (1.0 - q);
    }
    return q;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 p = fragCoord/iResolution.xy;

    vec3 col = vec3(LogisticIterations(p,8),LogisticIterations(p,9),LogisticIterations(p,7));

    // Output to screen
    fragColor = vec4(col,1.0);
}
