void cameraMotion(in vec2 fragCoord, inout vec3 sceneColor);

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec3 sceneColor;

    cameraMotion(fragCoord, sceneColor);

    vec2 center = iResolution.xy / 2.0;
    float dotSize = 50.0;
    float distanceToCenter = length(fragCoord - center);
    float dot = smoothstep(0.0, dotSize, dotSize - distanceToCenter);
    sceneColor = mix(sceneColor, vec3(0.0), dot); // Mix with black for the dot

    fragColor = vec4(sceneColor, 1.0);
}

void cameraMotion(in vec2 fragCoord, inout vec3 sceneColor)
{
    float squareSize = 0.5;
    vec2 center = iResolution.xy / 2.0;
    float angle = iTime;
    vec2 rotatedCoord = mat2(cos(angle), -sin(angle), sin(angle), cos(angle)) * (fragCoord - center);
    sceneColor = 0.5 + 0.5 * cos(rotatedCoord.xyx + vec3(0, 2, 4));
    float distanceToCenter = length(rotatedCoord);
    float smoothEdge = smoothstep(squareSize, squareSize + 0.01, distanceToCenter);
    sceneColor *= smoothEdge;
}
