#define PI 3.1415926538



float threshold = .6;
float curvature = 100.;



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

   float x = float( (uv.x * 2.) - 1.);

   float vX = pow(cos(PI * x / 2.), .5);

   float d = vX * (curvature * -.01);

   uv.y += d + iTime * -.5;


   float bands =  step(sin(25. * uv.y), threshold);




    // Time varying pixel color
    vec3 col = vec3(bands);

    // Output to screen
    fragColor = vec4(col, 1.);
}
