// Just a quick little thingy
// Took a little bit to figure out the phases though

#define PI 3.1415926536
#define TAU (2.0 * PI)

vec3 rot(vec3 v, vec3 k, float t)
{
    k = normalize(k);
    return mix(k*dot(k,v), v, cos(t)) + cross(k, v)*sin(t);
}

vec2 moon(vec2 uv, vec3 l, float amb)
{
    float l2 = dot(uv, uv);
    vec3 sphere = vec3(uv, sqrt(1.0 -  clamp(l2, 0.0, 1.0)));

    vec4 surf = vec4(0);
    float a = 1.0;
    float b = 1.0;
    for(float i = 0.0; i < 3.0; i++)
    {
        surf += texture(iChannel0, sphere * a * 0.05) * b;
        a *= 2.71828;
        b /= 2.71828;
    }

    return vec2((max(dot(normalize(sphere + (surf.xyz - 0.5) * 0.1), l), 0.0) + amb)*surf.w*surf.w*0.5, smoothstep(1.0, 1.0-64.0/iResolution.y, l2));
}

vec4 fbm(vec2 uv, vec2 o)
{
    vec4 r = vec4(0);
    float str = 1.0;
    float scale = 1.0;
    for(float i = 0.0; i < 3.0; i++)
    {
        r += texture(iChannel1, uv * scale * 0.004) * str;
        uv *= mat2(0.80,  0.60, -0.60,  0.80);
        uv += o;
        scale *= 2.0;
        str *= 0.5;
    }

    return r * 0.5;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord*2.0-iResolution.xy)/iResolution.y * 8.0;

    float moonRot = (iDate.x * 365.0 + iDate.y * 30.5 + iDate.z + iDate.w / 86400.0) - 738757.0;
    moonRot = mod(moonRot, 29.53059) / 29.53059 * TAU;
    vec3 lgt = rot(vec3(0, 0, 1), normalize(vec3(0.3, 1, 0)), -moonRot);

    vec2 moon = moon(uv, lgt, 0.002);
    vec4 clouds = fbm(uv*0.05 + vec2(0.025, 0.005) * iTime, vec2(0.5, 0.0) * iTime * 0.01);

    vec3 col = vec3(step(texture(iChannel2, uv*0.2).r * texture(iChannel2, uv*0.1).r, 0.1) * 0.0002 + 0.001);
    col = mix(col, moon.rrr, moon.g);

    float glow = 1.0 / pow((dot(uv, uv) * 0.2 + 1.0), 3.0) * (lgt.z*0.5 + 0.5);
    col += pow(smoothstep(0.5, 0.0, abs(clouds.r - 0.5)), 3.0) * glow * 0.02;

    col = mix(col, vec3(0.0001), smoothstep(0.2, 0.6, clouds.r) * 0.8);

    fragColor = vec4(pow(col, vec3(0.4545)), 1);
    fragColor.rgb += texelFetch(iChannel1, ivec2(fragCoord) % textureSize(iChannel1, 0).xy, 0).rgb/256.0;
}
