float sdPoint (vec2 p, vec2 c) {
    return length(p - c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    float col = 0.;

    for (int k = 0; k < N; k++) {
        vec2 center = texelFetch(iChannel0, ivec2(k, 0), 0).xy;
        col += (1. - smoothstep(0., 0.02, sdPoint(uv, center)));
    }

    col = pow(col, 1./2.2);
    fragColor = vec4(col, col, col, 1.);
}

// This shader is for a course, Fragment Shader Art (course code: ARTS1308) in ShanghaiTech University as hw10.
// Reference: https://www.shadertoy.com/view/XfXcDM
//
// This shader turned the N-body system into a particle group.
// Particles may attract or repulse each other, depending on their distance.
// In this shader:
//    in 0~10 seconds, the distance at which the direction of the force between the particles changes is 0.4
//    after 10 seconds, the distance is 0.05
// So, you'll see the particles first spread far away,and then get back together.

#define G 1e4
#define m 1.
#define N 100
#define PI 3.14159
#define v0 1.2e3
#define dt 1e-6
#define ZERO min(float(iFrame), 0.)
#define DAMPING 1e3

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.);

    int type = int(fragCoord.y);
    int index = int(fragCoord.x);

    if (index <= N) {
        if (iFrame < 5 && type <= 1) { // Initialize
                int w = int(iChannelResolution[1].x);
                vec2 rand = texelFetch(iChannel1, ivec2(index%w, index/w), 0).xy;
                float r = sqrt(0.7 * rand.x + 0.3) * 0.4;
                float t = 2. * PI * rand.y;
                if (type == 0) fragColor.xy =  r * vec2(cos(t), sin(t)); // position
                if (type == 1) fragColor.xy = v0 * vec2(-sin(t), cos(t)); // velocity
        }

        if (type == 2) { // Compute force
            vec2 force = vec2(0.);
            vec2 mypos = texelFetch(iChannel0, ivec2(index, 0), 0).xy;
            vec2 myvel = texelFetch(iChannel0, ivec2(index, 1), 0).xy;
            for (int j = 0; j < N; j++)
                if (j != index) {
                    vec2 pos = texelFetch(iChannel0, ivec2(j, 0), 0).xy;
                    vec2 diff = mypos - pos;
                    float d = length(diff);
                    d = max(2e-1, d);
                    float boundary = step(10., iTime)*-0.1575 + 0.16;
                    vec2 f = -normalize(diff) * log(d * d / boundary) * G / d / d;
                    force += f;
                }
            fragColor.xy = force - DAMPING * myvel;

        }

        if (iFrame >= 5 && type <= 1) { // Update
            vec2 force = texelFetch(iChannel0, ivec2(index, 2), 0).xy;
            vec2 vel = texelFetch(iChannel0, ivec2(index, 1), 0).xy;
            vec2 pos = texelFetch(iChannel0, ivec2(index, 0), 0).xy;

            vel += force * dt;
            pos += vel * dt + force * dt * dt / 2.;
            if (type == 0) fragColor.xy = pos;
            if (type == 1) fragColor.xy = vel;
        }
    }
}
