mat2 rot2D(float angle) {
    float s = sin(angle);
    float c = cos(angle);
    return mat2(c, -s, s, c);
}

vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

float sdSphere(vec3 p, float r) {
    return length(p) - r;
}

float sdBox(vec3 p, vec3 box) {
    vec3 q = abs(p) - box;
    return length(max(q,0.)) + min(max(q.x, max(q.y, q.z)), 0.0);
}


float map2(vec3 p) {
    p.z += iTime * 0.4; // move boxes towards the camera


    //p.z = mod(p.z, .25) - .125;
    p =  fract(p) - .5; // repeat through space

    float box = sdBox(p, vec3(.1));

    return box;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     vec2 uv = (fragCoord * 2. - iResolution.xy) / iResolution.y;

     // initialization
     vec3 ro = vec3(0,0,0); // ray origin
     vec3 rd = normalize(vec3(uv,1)); // ray direction
     vec3 col = vec3(0);

    float t = 0.;

    // ray marching
    for (int i = 0; i < 80; i++) {

    vec3 p = ro + rd * t; // position along the ray

    p.xy *= rot2D(t*.2);
    p.y -= cos(t) * 0.5;

    float d = map2(p); // current distance to the scene

    t += d;  // marching

    if (d < 0.001) break; // early exit if close enough to an object
    if (t > 100.) break;  // early exit if too far
 }

    col = palette(t, vec3(.5), vec3(0.5), vec3(0.5), vec3(0.5));

    // Output to screen
    fragColor = vec4(col, 1.0);
}
