float square(vec2 uv, vec2 p, vec2 s) {
  return step(p.x, uv.x)
    * (1. - step(p.x + s.x, uv.x))
    * step(p.y, uv.y)
    * (1. - step(p.y + s.y, uv.y));
}

vec3 sierpinski(vec2 uv) {
  const float T = 1. / 3.;
  float v = 0.;
  for (int i = 0; i < 6; i++) {
      v = max(v, square(uv, vec2(T), vec2(T)));
      uv = fract(uv * 3.);
  }
  return vec3(v);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = fragCoord / iResolution.xy;
  vec3 col = sierpinski(uv);
  fragColor = vec4(col,1.0);
}
