#define pi 3.1415926
#define tau (pi+pi)

mat3 yaw_pitch_roll(float yaw, float pitch, float roll)
{
    mat3 R = mat3(vec3(cos(yaw), sin(yaw), 0.0), vec3(-sin(yaw), cos(yaw), 0.0), vec3(0.0, 0.0, 1.0));
    mat3 S = mat3(vec3(1.0, 0.0, 0.0), vec3(0.0, cos(pitch), sin(pitch)), vec3(0.0, -sin(pitch), cos(pitch)));
    mat3 T = mat3(vec3(cos(roll), 0.0, sin(roll)), vec3(0.0, 1.0, 0.0), vec3(-sin(roll), 0.0, cos(roll)));

    return R * S * T;
}

#define DIST_MAX 1000.0

// based on https://iquilezles.org/articles/intersectors/
vec2 box_intersection(vec3 ro, vec3 rd, vec3 s) 
{
    vec3 m = 1.0/rd;
    vec3 n = m*ro;
    vec3 k = abs(m)*s;
    vec3 t1 = -n - k;
    vec3 t2 = -n + k;
    float tN = max(max(t1.x, t1.y), t1.z);
    float tF = min(min(t2.x, t2.y), t2.z);
    if(tN > tF || tF < 0.0) return vec2(DIST_MAX); // no intersection
    return vec2(tN, tF);
}

float sdSphere(vec3 p, float d)
{
    return length(p) - d;
}

float sdBox( vec3 p, vec3 b )
{
    vec3 d = abs(p) - b;
    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}
/*
vec4 get_voxel(ivec3 c)
{
    vec3 p = vec3(c) + vec3(0.5);
    float d = max(-sdSphere(p, 7.5), sdBox(p, vec3(6.0)));
    return d < 0.0 ? vec4(1,0,0,1) : vec4(0);
}
*/
vec4 get_voxel(ivec3 c)
{
    vec3 p = vec3(c) + vec3(0.5);
    if(p.z < -5.0) return vec4(1);
    float d = sdSphere(p, 5.5);
    float t = p.z - iTime;
    return d < 0.0 ? vec4(0.5+0.5*cos(t),0.5+0.5*sin(t),0,.5) : vec4(0);
}

vec4 blend(vec4 a, vec4 b)
{
    // over-operator as described in https://en.wikipedia.org/wiki/Alpha_compositing
    return vec4(a*a.w + b*(1.0-a.w)); // pre-multiplied
    // straignt alpha
    float o = a.w + b.w*(1.0-a.w);
    return vec4((a.xyz*a.w + b.xyz*(1.0-a.w))/o, o);
}

#define MAX_RAY_STEPS 64

vec4 traverse(vec3 ray_pos, vec3 ray_dir) // returns color and opacity
{
    vec4 current = vec4(0,0,0,0);

    // bounding box
    vec2 bounds = box_intersection(ray_pos, ray_dir, vec3(6));
    if(bounds.x >= DIST_MAX) return current;

    // for painter's algorithm
    {
        ray_pos += ray_dir * bounds.y; // snap ray to backside of box
        ray_dir = -ray_dir; // reverse direction
        bounds = vec2(bounds.y - bounds.x, 0.0); // adjust intersection to reverse view
    }

    // dda initialization (four lines) adapted from
    // https://www.shadertoy.com/view/4dX3zl Branchless Voxel Raycasting by fb39ca4
    // implementing https://lodev.org/cgtutor/raycasting.html
    ivec3 voxel_pos = ivec3(floor(ray_pos)); // round down position to get tile position
    vec3 delta_dist = 1.0 / abs(ray_dir); // reciprocal absolute ray direction
    ivec3 ray_step = ivec3(sign(ray_dir)); // sign of ray direction
    vec3 side_dist = (sign(ray_dir) * (vec3(voxel_pos) - ray_pos) + (sign(ray_dir) * 0.5) + 0.5) * delta_dist; // initial distances to nearest walls by dimension
    bvec3 nearest = bvec3(0);

    for(int i = 0; i < MAX_RAY_STEPS; i++)
    {
        // dda core (three lines) adapted from
        // https://www.shadertoy.com/view/4dX3zl Branchless Voxel Raycasting by fb39ca4
        // implementing https://lodev.org/cgtutor/raycasting.html
        bvec3 nearest_next = lessThanEqual(side_dist.xyz, min(side_dist.yzx, side_dist.zxy)); // component is true if nearest wall in that dimension
        vec3 side_dist_next = side_dist + vec3(nearest_next) * delta_dist; // sign of ray direction
        ivec3 voxel_pos_next = voxel_pos + ivec3(vec3(nearest_next)) * ray_step; // step to next vpxel in direction of nearest wall

        float enter_dist = length(vec3(nearest) * (side_dist - delta_dist));
        //if(enter_dist > bounds.x) break; // leave loop early if outside bounding box

        vec4 next = get_voxel(voxel_pos);
        if(next.w > 0.0)
        {
            float leave_dist = length(vec3(nearest_next) * (side_dist_next - delta_dist));
            float dist = leave_dist - enter_dist; // length of ray-traversal through cube
            dist /= sqrt(3.0); // normalize: traversal along long diagonal -> full opacity
            if(false)
            {
                dist = mix(1.0, dist, 0.5+0.5*cos(iTime*tau/2.0)); // switch between modes
                //current = vec4(dist);
                current = blend(vec4(next.xyz, next.w*dist), current); // NAIVE-LINEAR
            }
            else
            {
                if(cos(iTime*tau/2.0) > 0.0)
                {
                    //current = mix(next, current, exp2(-next.w / log(2.0) * dist)); // improved blending by spalmer
                    //current = mix(next, current, exp2(-next.w / (1.0/log2(exp(1.0))) * dist)); // improved blending by spalmer
                    current = mix(next, current, exp(-next.w * dist)); // improved blending by spalmer
                }
                else
                {
                    current = blend(next, current); // transparent boxes
                }
            }
        }

        if(enter_dist > bounds.x) break; // leave loop early if outside bounding box

        nearest = nearest_next;
        side_dist = side_dist_next;
        voxel_pos = voxel_pos_next;
    }

    return current;
}

void mainImage(out vec4 o, in vec2 I)
{
    bool demo = all(lessThan(iMouse.xy, vec2(10.0)));
    vec2 R = iResolution.xy;
    I = (2.0 * I - R) / R.y; // concise pixel-position mapping thanks to Fabrice
    float yaw = 2.0 * pi * float(iMouse.x) / float(R.x);
    //float pitch = pi - pi * float(iMouse.y) / float(R.y);
    float pitch = pi - pi / 2.0 * float(iMouse.y) / float(R.y);
    //float pitch = -pi*1.0 - 0.5 * pi * float(iMouse.y) / float(R.y);
    yaw = !demo ? yaw : 2.0 * pi * fract(iTime * 0.01);
    pitch = !demo ? pitch : 1.5 * pi/2.0;

    vec3 ray_pos = vec3(0.0, 0.0, -20.0);
    vec3 ray_dir = vec3(I.x, I.y, 2.0); // NOTE: un-normalized direction appears to work fine here!
    ray_dir = normalize(ray_dir); // normalize to facilitate later lighting computations

    mat3 M = yaw_pitch_roll(yaw, pitch, 0.0);

    ray_pos = M * ray_pos;
    ray_dir = M * ray_dir;

    vec4 color = traverse(ray_pos, ray_dir);

    color.xyz = tanh(color.xyz); // roll-off / soft-clamp overly bright colors
    color.xyz = sqrt(color.xyz); // approximate gamma
    //o = vec4(color.xyz, color.w);
    o = vec4(color.xyz, 1);
}
