void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;
    vec2 uv1 = vec2(abs(cos(uv.x)), abs(cos(uv.y + iTime * .5)));
    vec2 uv2 = uv;
    uv2.y -= 0.3;
    float d = length(uv2);
    float sun1 = cos(iTime * 1.5) * 7.;
    float sun2 = sin(iTime * 1.5) * 7.;
    d = smoothstep(abs(sin(iTime * 2.)) / 100. + (21. + sun1) / 100., abs(sin(iTime * 2.)) / 100. + (21. + sun2) / 100., d);
    vec3 col = vec3(uv1.x, d, d);

    if( uv.y < -0.20 && !(abs(uv.x ) - abs(uv.y * 2.) > 0.1)){

        col -= vec3(2.);
    }
    else if(uv.y < -0.20 && !(abs(uv.x ) - abs(uv.y * 2.) > 0.12)){
        col += vec3(1100.6, 1100.4, 1100.);
    }
    else if( uv.y < -0.19 && !(abs(uv.x ) - abs(uv.y * 2.) > 0.1)){


        col += vec3(1100.6, 1100.4, 1100.);
    }
    else if(uv.y < -0.19 && !(abs(uv.x ) - abs(uv.y * 2.) > 0.12)){
        col += vec3(1100.6, 1100.4, 1100.);
    }
    else if ( uv.y < -0.01 - abs(cos(uv.x * 4. + iTime) * sin(uv.x * 3. + iTime) / 30.) ){
        float d = smoothstep(0.1, -0.3, uv.y);
        col += vec3(-50., 0., -50.);
        if(int(fragCoord.x * fragCoord.y) % ( 1) == 0 )


            col += vec3(-50., 1. * d , 400. * d);
    }
    else if (uv.y < 0.9){
        float d = smoothstep(0.5, -0.1, uv.y);
        col += vec3(0., 0.7 * d , 0.);
    }

    if ( abs(uv.x ) < 0.05 * abs(uv.y) && uv.y  < -0.20 ) {
        col += vec3(5.);
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
