void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float speed = 5.;
    float waveLength = 7.;
    float size = .04;
    float wave = speed * iTime + waveLength * uv.x;

    float offset = size*sin(wave);

    uv.y -= offset;

    float aspectRatio = iResolution.y/iResolution.x;

    uv -= vec2(0.5);
    uv = uv * vec2(1,aspectRatio);  

    float cost=cos(iTime+uv.x*1.5)*1.0;
    float fs=1.0+cost*0.4;

   if(length(uv) < .15)
   {

        fragColor = vec4(1,0,0,0) * fs;
   }
   else
   {
         fragColor = vec4(1.0)* fs;
   }

    //corte vertical
    if(uv.y<(0.04*offset- 0.25)){

        fragColor = vec4(0);
    }
    //corte vertical
    if(uv.y>(0.04 * offset + 0.25)){
        fragColor = vec4(0);
    }
    if(uv.x<-0.4){
     fragColor = vec4(0);
    }
    if(uv.x>0.4){
     fragColor = vec4(0);
    }



}
