void mainImage(out vec4 fragColor, in vec2 fragCoord){
  float linewidth = 0.07, amplitude = 3.7, scale = 15.0, xrange = scale*iResolution.x/iResolution.y/1.8;
  vec2 uv = (fragCoord.xy*scale - iResolution.xy*scale*0.5)/iResolution.y;
  vec3 col = vec3(0.0), lc1 = col, lc2 = col, lc3 = col, lc4 = col, cc1 = col;
  float x = sin(uv.x-iTime);
  x += sin(x*1.2);
  x += sin(x*3.2);
  x += sin(x*4.2*sin(iTime*0.5)*1.25);
  lc1.r = 1.0-step(linewidth, abs(uv.y-(sin(uv.x-iTime)+sin(uv.y-iTime))*amplitude));
  lc2.g = 1.0-step(linewidth, abs(uv.y-x));
  lc3.b = 0.9-smoothstep(6.5, abs(sin(iTime))*6.49, abs(uv.y-sin(uv.x-iTime))*1.1);
  lc4.rg = vec2(1.0-step(linewidth, abs(uv.y-(sin(uv.x-iTime)+sin(uv.x))*amplitude)));
  cc1 = clamp(vec3(1.0, 0.65, 0.0)-vec3(length(uv-vec2(-xrange+mod(iTime, xrange*2.0), sin(iTime)*5.0))-0.3-abs(1.1*sin(iTime))), 0.0, 1.0);
  col = lc1 + lc2 + lc3 + lc4 + cc1;
  fragColor = vec4(col, 1.0);
}
