float scree(float x){
return pow(2.0,-pow((x-2.0),2.0))+1.0;
}
float random (in vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))
                * 43758.5453123);
}

// Value noise by Inigo Quilez - iq/2013
// https://www.shadertoy.com/view/lsf3WH
float noise(vec2 st) {
st=st*3.0;
    vec2 i = floor(st);
    vec2 f = fract(st);
    vec2 u = f*f*(3.0-2.0*f);
    return mix( mix( random( i + vec2(0.0,0.0) ),
                     random( i + vec2(1.0,0.0) ), u.x),
                mix( random( i + vec2(0.0,1.0) ),
                     random( i + vec2(1.0,1.0) ), u.x), u.y);
}
float lines(in vec2 pos, float b){
    float scale = 10.0;
    pos *= scale;
    return smoothstep(0.0,
                    .5+b*.5,
                    abs((sin(pos.x*3.1415)+b*2.0))*.5);
}
mat2 rotate2d(float angle){
    return mat2(cos(angle),-sin(angle),
                sin(angle),cos(angle));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-iResolution.xy*.5)/iResolution.xy;

  vec2 uv3=uv;
   uv.y*=((0.01/(iTime*iTime))+1.0);
    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
 uv*=sqrt(distance(uv,vec2(0,0))*2.0+1.0)*0.82;
    vec2 uv2=vec2(floor(uv.x*800.0/3.14*5.0)/(800.0/3.14*5.0),floor(uv.y*500.0/3.14)/(500.0/3.14));
    if((uv3.x*uv3.x*uv3.x*uv3.x*uv3.x*uv3.x)+(uv3.y*uv3.y*uv3.y*uv3.y*uv3.y*uv3.y)<0.005&&abs(uv.y)<1.0){
    vec4 t = texture( iChannel0, uv2 +vec2(0.5));
  if(sin(uv.x*700.0)-0.8>0.0){
    fragColor.r = t.r*min((sin(uv.x*700.0)-0.8)*(sin(uv.y*400.0)+1.19)*8.0,1.0)*((0.5/iTime)+1.0)*scree(mod((uv.y*20.0+iTime*8.0-10.0),(20.0)));
   }
    if(sin(uv.x*700.0+8.5)-0.8>0.0){
    fragColor.g = t.g*min((sin(uv.x*700.0+8.5)-0.8)*(sin(uv.y*400.0)+1.19)*8.0,1.0)*((0.5/iTime)+1.0)*scree(mod((uv.y*20.0+iTime*8.0-10.0),(20.0)));
   }
   if(sin(uv.x*700.0+17.0)-0.8>0.0){
    fragColor.b = t.b*min((sin(uv.x*700.0+17.0)-0.8)*(sin(uv.y*400.0)+1.19)*8.0,1.0)*((0.5/iTime)+1.0)*scree(mod((uv.y*20.0+iTime*8.0-10.0),(20.0)));
   }
   }else{
   if((uv3.x*uv3.x*uv3.x*uv3.x*uv3.x*uv3.x)+(uv3.y*uv3.y*uv3.y*uv3.y*uv3.y*uv3.y)>0.006){
   vec2 pos=uv3;
    pos = rotate2d( noise(pos) ) * pos;

    // Draw lines
    float pattern=pos.x;
    pattern = lines(pos*4.0,0.6);
   fragColor.rgb=vec3(0.4,0.23,0.05)*(pattern/2.0+0.5)/1.5;
   }else{
   fragColor.rgb=vec3(0.0);
   }
   }
    fragColor.a=1.0;
}

