// Fork of "Spiky Purple Alien Blob" by RedShip. https://shadertoy.com/view/stsczn
// 2024-01-15 02:28:55


#define spikeNum 12.
#define radiusCircle 0.35

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec3 baseColor = vec3(1.0, 0.0, 0.0); // 给定的颜色，这里为红色

    float spikeSize = 0.03 * abs(sin(1.5)) + 0.07;

    float thi = atan(uv.x, uv.y);
    float r = radiusCircle + spikeSize * sin(thi * spikeNum);

    float circle = smoothstep(r, r + 0.002, distance(uv, vec2(0.)));

    float violet = smoothstep(r + 0.2, 0., distance(uv, vec2(0.)));
    vec3 violetcol = vec3(0.8, 0.4, 1.);

    float centercircle = smoothstep(r, 0.102, distance(uv, vec2(0.)));
    vec3 centercol = vec3(0.8, 0.8, 0.5);

    float origin = smoothstep(0.1, -0.1, distance(uv, vec2(0.)));
    vec3 origincol = vec3(0.8, 0.5, 0.);

    vec3 starColor = mix(baseColor.bgr, baseColor.rgb, fract(thi / (2.0 * 3.14159265359)));

    vec3 col = circle + violet * violetcol + centercircle * centercol + origin * origincol;
    col *= starColor;

    fragColor = vec4(col, 1.0);
}
