void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 n = fragCoord / iResolution.xy;
    float ncos = cos(iTime) * 0.5 + 0.5;
    float nsin = sin(iTime) * 0.5 + 0.5;

    vec4 horizontalPattern = vec4(n.x * ncos, 0.0, 0.0, 1.0);
    vec4 verticalPattern = vec4(n.y * nsin, 0.0, 0.0, 1.0);

    fragColor = horizontalPattern + verticalPattern;





}
