void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // main circle pos, right circle pos, right dot pos ...
    vec2 mcp = iMouse.xy + vec2(0, sin(iTime * 3.) * 10.0);
    float r = 80.0;

    vec2 rcp = mcp + r / 1.5 - vec2(0, sin(iTime * 3.) * 20.0);
    float rcr = r / 2.0;

    vec2 rdp = rcp + rcr / 1.5 - vec2(0, sin(iTime * 3.) * 30.0);
    float rdr = r / 4.0;

    vec2 lcp = mcp - r / 1.5 - vec2(0, sin(iTime * 3.) * 20.0);
    float lcr = r / 2.0;

    vec2 ldp = lcp - lcr / 1.5 - vec2(0, sin(iTime * 3.) * 30.0);
    float ldr = r / 4.0;

    // eyes/iris
    vec2 lep = mcp - vec2(10.0, 40.0) + vec2(sin(iTime * 3.0) * 20.0);
    vec2 rep = mcp + vec2(50.0, 0) + vec2(sin(iTime * 3.0) * 20.0);
    vec2 lip = lep + vec2(sin(iTime * 3.0) * 5., sin(iTime * 3.0) * 15.0);
    vec2 rip = rep + vec2(sin(iTime * 3.0) * 5., sin(iTime * 3.0) * 15.0);

    // variables
    vec4 bg = vec4(0, 0, 0, 1);
    vec4 elementColor = vec4(0.0, .7, .95, 1);
    vec4 edgeColor = vec4(0.0, 0.5, 0.7, 1);
    float eyeRad = 30.0;
    float irisRad = 10.0;
    float dm = distance(fragCoord, mcp);
    float drc = distance(fragCoord, rcp);
    float drd = distance(fragCoord, rdp);
    float dlc = distance(fragCoord, lcp);
    float dld = distance(fragCoord, ldp);
    float dle = distance(fragCoord, lep);
    float dre = distance(fragCoord, rep);
    float dri = distance(fragCoord, rip);
    float dli = distance(fragCoord, lip);
    float edgeRad = 10.0;

    fragColor = vec4(0.05, 0.05, 0.1, 1);


    // Draw edges
    float minDistToAnyComponent = min(dm - r, min(drc - rcr, min(drd - rdr, min(dlc - lcr, dld - ldr))));
    if (minDistToAnyComponent < edgeRad) {
        fragColor = edgeColor;
    }

    // Draw body
    if (dm < r) {
        fragColor = elementColor;
    }
    if (drc < rcr) {
        fragColor = elementColor;
    }
    if (drd < rdr) {
        fragColor = elementColor;
    }
    if (dlc < lcr) {
        fragColor = elementColor;
    }
    if (dld < ldr) {
        fragColor = elementColor;
    }

    // Draw eyes
    if (dre < eyeRad) {
        fragColor = vec4(0.8);
    }
    if (dle < eyeRad) {
        fragColor = vec4(0.8);
    }
    if (dri < irisRad) {
        fragColor = vec4(0.1);
    }
    if (dli < irisRad) {
        fragColor = vec4(0.1);
    }
}
