vec2 CenterUV(vec2 fragCoord )//uv的居中与反拉伸https://www.shadertoy.com/view/M3cSDS
{
    return (2.0 * fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = CenterUV(fragCoord );
    vec2 sinuv = vec2(uv.y ,sin(uv.x+iTime *1.));

    float t = 1.-smoothstep(.5,.7,length(sinuv));

    sinuv.x /=sin(iTime);
    t += pow(1.-length(sinuv),0.2);
    t = pow(t,2.);
    t *= step(length(sinuv),1.);

    vec3 col2 = 0.5 + 0.5*cos(iTime+vec3(sinuv.x,sinuv.y,uv.x)+vec3(0,2,4)) * t;

    fragColor = vec4(col2,1.);
}
