/////////"sdRoundBox"function cited from: https://www.shadertoy.com/view/4llXD7

#define PI 3.14159265359

float sdRoundBox( in vec2 p, in vec2 b, in vec4 r ) 
{
    r.xy = (p.x>0.0)?r.xy : r.zw;
    r.x  = (p.y>0.0)?r.x  : r.y;
    vec2 q = abs(p)-b+r.x;
    return min(max(q.x,q.y),0.0) + length(max(q,0.0)) - r.x;
}

float sdPygal(vec2 p, float b, float t)
{
   return sdRoundBox(p+vec2(0., sin(t)*0.1), vec2(0.4, 0.3)+b, vec4(0.2)+b);
}

float sdButt(vec2 p, float b, float t)
{
   return sdRoundBox(p-vec2(0.3+sin(t)*0.1,cos(t)*0.1), vec2(0.3-cos(t+PI*0.5)*0.05, 0.3-sin(t)*0.05)+b, vec4(0.1)+b*0.3);
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    float t = iTime*10.;

    float dP = sdPygal(uv, 0., t);

    float dB = sdButt(vec2(abs(uv.x), uv.y), 0., t);

    float outLine = smoothstep(0., -0.01, min(dP, dB)); 

    float sB = smoothstep(0., 0.01, dB);
    float buttLine = smoothstep(0., -0.01, dB); 

    float dL = sdButt(vec2(abs(uv.x-0.125),uv.y)+vec2(0.,-0.15), -0.2, t);
    dL = smoothstep(0.01,0.,dL)*0.5;


    float l = 1.-mix(buttLine, 1., sB);

    vec3 c = mix(vec3(.8,0.5,0.1), mix(vec3(0.,0.25,0.3)+dL, vec3(0.), l), outLine);

    fragColor = vec4(c,1.);
}
