    Q = vec4(0);
        vec2 V = vec2(0);
        for (float x = -3.;x <= 3.; x++)
        for (float y = -3.;y <= 3.; y++) {

            vec2 u = vec2(x,y);

            vec4 q = A(U+u);
            vec2 v = unpackSnorm2x16(uint(q.z));
            v +=q.xy;
            float w = 1.;
            if(q.w>0.) {
                w = .25;
                float h = 2.*3.14159265359*hash44(vec4(U+u,iTime,iFrame)).x;
                vec2 o =.51*vec2(cos(h),sin(h));
                if (cell(u+v+o)) {
                    Q.xy += q.xy*w*q.w;
                    V += (u+v+o)*w*q.w;
                    Q.w += w*q.w;
                }
                if (cell(u+v-o)) {
                    Q.xy += q.xy*w*q.w;
                    V += (u+v-o)*w*q.w;
                    Q.w += w*q.w;
                }
                w = .5;
            }
            if (cell(u+v)) {
                Q.xy += q.xy*w*q.w;
                V += (u+v)*w*q.w;
                Q.w += w*q.w;
            }
        }
        if (Q.w>0.) {
            Q.xy/=Q.w;
            V /= Q.w;
        }

        Q.z = float(packSnorm2x16(V.xy));

}

