#define h11(a) fract(sin(a) * 437518.56453) + .1
#define pi acos(-1.)
#define t iTime

void mainImage(out vec4 o, vec2 u) {
    vec2 r = iResolution.xy;

    u = (u + u - r) / r.y;
    u = vec2(
        atan(u.x, u.y) * 12. / 3.14,
        length(u) / mix(1., dot(u, u),  smoothstep(-1.5, 1.5, sin(t * .12)))
    );

    float i, j, hh,
          BD = 1.2, 
          SY = 5.,
          TS = .1;

      i = floor(u.x);
    u.x = fract(u.x);

    if(u.y + fract(iTime * TS * SY) / SY - BD < 0.) {

        u.y = 
              + u.y  * SY 
              + iTime * TS * SY

              ;

        u.y = fract(u.y);

    } else {

        u.y = 
              + u.y 
              + fract(iTime * TS * SY) / SY 
              - fract(iTime * TS * SY)
              - BD
              + BD * SY
              + iTime * TS * SY

              ;

        j = floor(u.y);

        hh = h11(i + h11(j));

        u.y = fract(u.y) * SY
                  + min(0., (- SY + hh * mod(iTime * SY * SY,  SY / TS)));
    }

    o = vec4(smoothstep(20. / r.y, 0., length(u - .5) - .49)) / length(u+1.);
}
