void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = texture(iChannel0, uv).rgb;
    float mono = dot(col, vec3(0.299f, 0.587f, 0.114f));
    float t = (sin(iTime) + 1.0) * 0.5;
    fragColor = vec4(mix(col, vec3(mono, mono, mono), t),1.0);
}
