void mainImage( out vec4 o, in vec2 FC ){

float i,j,d=.1,e; vec3 p;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*iResolution.xy)/iResolution.y,e);
    p.z-=2.-iTime*.3;

    for(j=0.;j++<2.;)
        p=abs(abs(mod(cross(p,vec3(-.4,-.1,.6)),i*.2))-.03)-.02,
        d=dot(length(p.xy),p.z)-.05;

    e+=d;

}o+=vec4(sqrt(p),1)*5./i;
}
