// cosine based palette, 4 vec3 params
vec3 palette( in float t )
{
    vec3 a = vec3(0.1, 0.9,0.8);
    vec3 b = vec3(0.2,0.4,0.7);
    vec3 c = vec3(1.0,1.0,1.0);
    vec3 d = vec3(0.0, 0.33, 0.56);
    return a + b*cos( 6.28318*(c*t+d) );
}

float heart(vec2 p) {
    // Center it more, vertically:
    p.y += .6;
    // This offset reduces artifacts on the center vertical axis.
    const float offset = .3;
    // (x^2+(1.2*y-sqrt(abs(x)))^2âˆ’1)
    float k = 1.2 * p.y - sqrt(abs(p.x) + offset);
    return p.x * p.x + k * k - 1.;
}

float random (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord*2.0-iResolution.xy)/iResolution.y;

    vec3 finalCol = vec3(1.0);

    float d = heart(uv*2.0)*exp(-0.25*length(uv));

    vec3 col = -palette(length(uv*0.5)-iTime*0.05)*1.0+0.2;


    d= sin(d*4.0 -iTime)/2.0;
    d = abs(d);

    vec3 vignette = (pow(length(uv*0.5),4.0)*vec3(0.035,0.243,0.208));
    vec3 noise =random(uv)*col*0.5;
    finalCol += (col-noise)*d -  vignette;


    //increase contrast
    finalCol =  ((finalCol-0.5)*1.05)+0.5;


    fragColor = vec4(finalCol,1.0);
}
