#define LAYERS 5

// Pseudo-random function
float random(vec2 st) {
    return fract(sin(dot(st.xy, vec2(12.9898,78.233))) * 43758.5453123);
}

// 2D Noise function
float noise(vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));
    vec2 u = f * f * (3.0 - 2.0 * f);
    return mix(a, b, u.x) + (c - a)* u.y * (1.0 - u.x) + (d - b) * u.x * u.y;
}

// Function to generate a layer
vec4 generateLayer(vec2 uv, float depth, float time) {
    float speed = mix(0.1, 1.0, depth);
    float scale = mix(0.5, 2.0, depth);

    vec2 movement = vec2(time * speed, 0.0);
    vec2 scaledUV = uv * scale;
    vec2 movedUV = scaledUV + movement;

    float n = noise(movedUV * 10.0);

    vec3 color = mix(vec3(0.5, 0.8, 1.0), vec3(0.2, 0.5, 0.8), depth);
    float alpha = smoothstep(0.4, 0.6, n);

    return vec4(color, alpha);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;  // Correct for aspect ratio

    vec2 mouseUV = iMouse.xy / iResolution.xy;
    float time = iTime * 0.1;

    vec4 finalColor = vec4(0.0);

    for (int i = 0; i < LAYERS; i++) {
        float depth = float(i) / float(LAYERS - 1);
        float layerTime = time + depth * 10.0;  // Offset time for each layer

        // Apply parallax offset based on mouse position
        vec2 offset = (mouseUV - 0.5) * (1.0 - depth) * 0.1;
        vec2 offsetUV = uv + offset;

        vec4 layerColor = generateLayer(offsetUV, depth, layerTime);

        // Blend layer
        finalColor = mix(finalColor, layerColor, layerColor.a * (1.0 - depth));
    }

    // Add a subtle vignette effect
    float vignette = length(uv - 0.5);
    vignette = smoothstep(0.5, 1.5, vignette);
    finalColor.rgb *= 1.0 - vignette * 0.5;

    fragColor = finalColor;
}
