#define h21(a) fract(sin(dot(a, vec2(12.9898, 78.233))) * 43758.5453)
#define t iTime

void mainImage(out vec4 o, vec2 u) {    
    o *= 0.;
    vec2 r = iResolution.xy,
         p = u, f = r, 
         e, q, s, k;

    float i, T; 
    while(i++ < 4.)
        T = t + h21(k),
        q = sin(
                 s * (floor(T) 
                         + smoothstep(.2, .8, fract(T)) ) 
                 + h21(k) * 6.3
            ) * .2 + .5,

        q = mix(e, f, q),
        s = step(p, q),
        f = mix(f, q, s),
        e = mix(q, e, s),
        k += s * 3.14 + i;

    q = (f - e) * .5;
    q -= abs(p - e - q);
    o = smoothstep(0., 1., min(q.x, q.y))
                 * cos(h21(k) * vec4(1, 2, 3, 0) * length(q - length(q)) * .2);
}
