//#define draw(q) o *= smoothstep(0.0, 0.01, abs(q))
#define draw(q) o *= smoothstep(0.0, length(fwidth(I)), abs(q)) /* is fwidth supported on all devices? */

float dot2(vec2 v)
{
    return dot(v,v);
}

// Solution found here:
// https://math.stackexchange.com/questions/213658/get-the-equation-of-a-circle-when-given-3-points
// https://web.archive.org/web/20161011113446/http://www.abecedarical.com/zenosamples/zs_circle3pts.html
// (glsl implementation by jt)
// See also: Triangle https://en.wikipedia.org/wiki/Circumcircle
vec3 circle_through_three_points(vec2 a, vec2 b, vec2 c) // https://www.shadertoy.com/view/4csyDX three points circle
{
    vec4 A = vec4(dot2(a),a,1);
    vec4 B = vec4(dot2(b),b,1);
    vec4 C = vec4(dot2(c),c,1);
    // if only glsl supported swizzling matrix vectors...
    float M11 = determinant(mat3(A.yzw, B.yzw, C.yzw));
    float M12 = determinant(mat3(A.xzw, B.xzw, C.xzw));
    float M13 = determinant(mat3(A.xyw, B.xyw, C.xyw));
    float M14 = determinant(mat3(A.xyz, B.xyz, C.xyz));
    return 0.5*vec3(+M12,-M13,+sqrt(M12*M12+M13*M13+4.0*M14*M11))/M11;
}
/*
vec3 circle_through_three_points(vec2 a, vec2 b, vec2 c) // suggested by ENDESGA
{
    vec2 t = b - a;
    b = c - a;
    float d = 2. * ( t.x * b.y - t.y * b.x );
    c = t * dot( b, b );
    b *= dot( t, t );
    t = vec2( b.y - c.y, c.x - b.x ) / d;
    return vec3( t + a, length( t ) );
}
*/
void mainImage(out vec4 o, in vec2 I) // [-1,+1]
{
    vec2 R = iResolution.xy;
    I = 2.0*(I+I-R)/R.y;

    o = vec4(1);

    {
        vec2 a = vec2(-1.3, -0.2)+0.7*vec2(cos(iTime/2.0),sin(iTime/2.0));
        vec2 b = vec2(+1.1, -0.7)+0.5*vec2(cos(iTime/3.0),sin(iTime/3.0));
        vec2 c = vec2(+0.1, +1.4)+0.3*vec2(cos(iTime/5.0),sin(iTime/5.0));
        draw(length(I - a) - 0.02);
        draw(length(I - b) - 0.02);
        draw(length(I - c) - 0.02);

        vec3 d = circle_through_three_points(a, b, c);
        draw(length(I - d.xy) - d.z);
        draw(length(I - d.xy) - 0.01);
    }
}
