#define rot(a) mat2(cos(a + vec4(0, 11, 33, 0)))

// tks fabrice â™¥
void mainImage(out vec4 o, vec2 u) {    

    float t = iTime, x, i, j, d = 1.;        
    vec3  r = iResolution,
          D = normalize(vec3(u + u - r.xy, 4. * r.y)), q,
          p = vec3(cos(t * .2), sin(t * .3) / 2., -7);

    o = o / o;
    while(i++ < 1e2 && d > 4e-3) {
        d = .08 * length(q = p) - .01;
        q.zx *= rot(cos(.2 * t));

        for(j = 1.; j < 9.; j += j)
            d += abs(dot(sin(3. * q * j), r/r)) / j * - .04;

        o -= d < .008
                ? vec4(2, 3, 6, 0) / 5e2
                : vec4(.005);

        d = max(abs(d) - .003, -q.z - cos(t) - .5);
        p += d * D;
    }

    o *= o * o;
}

