void mainImage( out vec4 fragColor, in vec2 fragCoord )

{

    // Normalized pixel coordinates (from 0 to 1)

    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel colo

    vec3 col = cos(iTime+uv.yyy * uv.yyy + uv.xxx * uv.xxx + vec3(0, 30, 60)) * cos(iTime+uv.yyy * uv.yyy + uv.xxx * uv.xxx + vec3(0, 30, 60));

    // Output to screen

    fragColor = vec4(col,1.0);

}
